{"name": "Rink_Visitors", "description": "This table contains information about customers who visit the roller skating rink", "columns": "Visitor_ID (Primary Key), First_Name, Last_Name, Age, Gender, Date_of_Visit, Time_of_Visit (timestamp)."}
{"name": "Visitor_Activity", "description": "This table contains information about the activities of each visitor at the roller skating rink", "columns": "Activity_ID (Primary Key), Visitor_ID (Foreign Key), Activity_Type (e.g. Skating, Eating, Shopping), Duration, Cost, Activity_Date (timestamp)."}
{"name": "Merchandise", "description": "This table contains information about merchandise sold at the roller skating rink", "columns": "Merchandise_ID (Primary Key), Item_Name, Price, Quantity_Sold, Date_Sold (timestamp)."}
{"name": "Rink_Employees", "description": "This table contains information about employees who work at the roller skating rink", "columns": "Employee_ID (Primary Key), First_Name, Last_Name, Position, Date_Hired (timestamp)."}
{"name": "Revenue", "description": "This table contains information about revenue generated by the roller skating rink", "columns": "Revenue_ID (Primary Key), Day/Week/Month/Year (timestamp), Admission_Revenue, Merchandise_Revenue, Total_Revenue."}