{"name": "Airport", "description": "This table contains information about the airports, such as their name and location", "columns": "ID (primary key), Name, City, Country, Latitude, Longitude."}
{"name": "Airline", "description": "This table contains information about the airlines that use the airport", "columns": "ID (primary key), Name, Logo."}
{"name": "Route", "description": "This table contains information about the specific routes between two airports that an airline operates", "columns": "ID (primary key), AirlineID (foreign key to Airline Table), OriginAirportID (foreign key to Airport Table), DestinationAirportID (foreign key to Airport Table). "}
{"name": "PassengerVolume", "description": "This table contains information about the number of passengers on a particular route in a given period of time", "columns": "ID (primary key), RouteID (foreign key to Route Table), DateStarted (timestamp), DateEnded (timestamp), NumberOfPassengers. "}
{"name": "AircraftType", "description": "This table contains information about the type of aircraft used on a particular route", "columns": "ID (primary key), RouteID (foreign key to Route Table), AircraftTypeCode, Description. "}
{"name": "FlightDelays", "description": "This table contains information about any flight delays or cancellations for a particular route in a given period of time", "columns": "ID (primary key), RouteID (foreign key to Route Table), DateStarted (timestamp), DateEnded (timestamp), DelayMinutes, CancelledFlights."}