{"name": "User Preferences", "description": "This table contains users\u2019 preferences for Samsung phone models", "columns": "user_id (foreign key to Users table), preferred_phone_model, preferred_features, average_price_willing_to_pay, created_at, updated_at."}
{"name": "Phone Models", "description": "This table contains information about Samsung phone models", "columns": "model_id (primary key), model_name, operating_system, release_date, price, created_at, updated_at."}
{"name": "Features", "description": "This table contains information about features available on Samsung phone models", "columns": "feature_id (primary key), feature_name, description, created_at, updated_at."}
{"name": "User Phones", "description": "This table contains a record of the phones owned by each user", "columns": "user_phone_id (primary key), user_id (foreign key to Users table), model_id (foreign key to Phone Models table), purchase_date, created_at, updated_at."}
{"name": "User Reviews", "description": "This table contains reviews written by users about their Samsung phones", "columns": "review_id (primary key), user_id (foreign key to Users table), model_id (foreign key to Phone Models table), review, rating, created_at, updated_at."}
{"name": "Feature Ratings", "description": "This table contains ratings given by users for specific features of Samsung phones", "columns": "feature_rating_id (primary key), user_id (foreign key to Users table), feature_id (foreign key to Features table), rating, created_at, updated_at."}